package org.coforge.strings;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifferenceInStrings {
    public static void main(String[] args) {
        String s = "";
        String t = "";
        System.out.println(getDifference1(s, t));
    }

    static char getDifference1(String s, String t) {
        int ssum = 0, tsum = 0;
        if(s.isBlank() && t.length()==1){
            return t.charAt(0);
        }
        for (char ch : s.toCharArray()) {
            ssum += ch;
        }
        for (char ch : t.toCharArray()) {
            tsum += ch;
        }
        return (char) (tsum - ssum);
    }

    static char getDifference(String s, String t) {
        Map<Character, Integer> smap = new HashMap<>();
        Map<Character, Integer> tmap = new HashMap<>();
        if (t.isBlank() && t.length() == 1) {
            return t.charAt(0);
        }
        char c = '\0';
        for (Character ch : s.toCharArray()) {
            smap.put(ch, tmap.getOrDefault(ch, 0) + 1);
        }

        for (Character ch : t.toCharArray()) {
            tmap.put(ch, tmap.getOrDefault(ch, 0) + 1);
        }
        for (Character ch : t.toCharArray()) {
            if (tmap.containsKey(ch) && tmap.get(ch) > 0) {
                c= ch;
            }
        }

        return c;
    }
}
